import React, { useEffect, useState } from 'react'
import Modal from './Modal'
import { AnimatePresence } from 'motion/react'
import LoadingButton from './LoadingButton';
import { ClipLoader } from 'react-spinners';
import { PDFViewer, Page, Text, View, Document, StyleSheet, Image, pdf , Font } from '@react-pdf/renderer';
import { getOrder } from '../../services/ApiActions';
import MyFadeLoader from '../ui/MyFadeLoader'
import buffer from 'buffer'
window.Buffer = window.Buffer || buffer.Buffer; 

function SaleInvoiceComponent({isOpen, onClose, orderId}) {
  
  const base64Img = "data:image/png;base64,"
  const [isLoading, setIsLoading] = useState(false);
  const [isDocLoading, setIsDocLoading] = useState(true);
  const [invoice, setInvoice] = useState(null);


  useEffect(() => {

    const fetchOrder = async() => {
      try {
        
        const data = await getOrder(orderId);
        
        setupInvoice(data)
        
      } catch (error) {
        console.log(error)
      }
    }
    
    if(isOpen) fetchOrder()

  },[isOpen])

  Font.register({
    family: 'NotoSans',
    fonts: [
      {
        src: 'https://raw.githubusercontent.com/googlefonts/noto-fonts/main/hinted/ttf/NotoSans/NotoSans-Regular.ttf',
        fontWeight: 'normal',
      },
      {
        src: 'https://raw.githubusercontent.com/googlefonts/noto-fonts/main/hinted/ttf/NotoSans/NotoSans-Bold.ttf',
        fontWeight: 'bold',
      },
    ],
  });

  const setupInvoice = (data) => {
  
    let invoiceData;
    const items = [];
    let subTotal = 0, taxes = 0, discounts = 0;
    for(const item of data?.cartItems){

      const discount = item?.appliedOffer?.appliedAmount || 0;

      const newItem = item?.status === 'cancelled' ? null : {
        id: item?._id,
        name: item?.name,
        quantity: item?.quantity,
        price: item?.price,
        tax: item?.tax,
        discount,
        total: item?.price * item?.quantity + item?.tax
      }

      if(newItem) {

        subTotal += newItem?.price * newItem?.quantity;
        taxes += newItem?.tax;
        discounts += newItem?.discount;

        items.push(newItem)
      }
    }

    if(data?.appliedCoupon) discounts += data?.appliedCoupon?.appliedAmount;
    if(data?.cartOffer) discounts += data?.cartOffer?.appliedAmount;

    const rawTotal = subTotal + taxes - discounts;
    invoiceData = {
      items,
      shipTo: data?.shippingAddress,
      billTo: data?.billingAddress,
      invoiceDate: new Date(),
      totals: {
        subTotal,
        taxes,
        discounts,
        roundOff: rawTotal - Math.floor(rawTotal),
        total: Math.floor(rawTotal)
      }
    }

    setInvoice(invoiceData)
  }

  const handleclose = () => {
    setIsLoading(false);
    setIsDocLoading(true);
    setInvoice(null);
    onClose();
  }

  const styles = StyleSheet.create({
    doc: {
      width: '100%',
      height: '100%'
    },
    page: {
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'space-between',
      fontSize: 12,
      fontFamily: 'NotoSans'
    },
    section: {
      display: 'flex'
    },
    header: {
      display: 'flex',
      flexDirection: 'row',
      justifyContent: 'space-between',
      width: '100%',
      padding: '0 30px'
    },
    title: {
      fontWeight: 'bold',
      padding: '20px 0',
      marginBottom: 30,
      textAlign: 'center',
      fontSize: 20
    },
    address: {
      fontSize: '12px'
    },
    content: {
      display: 'flex',
      flexDirection: "column",
      padding: 30
    },
    tableRow: {
      display: 'flex',
      flexDirection: "row",
      padding: 5
    },
    totals: { 
      width: '100%',
      alignItems: 'flex-end',
      marginTop: 10,
      paddingTop: 10,
      borderTop: '1px solid #dfdfdf' 
    },
    footer: {
      width: '100%',
      borderTop: '1px solid #dfdfdf',
      fontSize: 10,
      marginTop: 10,
      paddingTop: 5,
      paddingBottom: 15
    }
  })

  const Doc = (
    <Document onRender={() => setIsDocLoading(false)}>
      <Page size="A4" style={styles.page}>
        
        <View>
          <Text style={styles.title}>INVOICE</Text>

          {/* header */}
          <View style={styles.header}>
            {/* left */}
            <View style={styles.section}>
              <Text style={{ marginBottom: 10 }}>No: #NV-2025-001</Text>
              <Text style={{ fontWeight: 'bold', marginBottom: 5 }}>Bill to:</Text>
              <Text style={styles.address}>Site at Canara Bank Jn,</Text>
              <Text style={styles.address}>Vengara, Malappuram,</Text>
              <Text style={styles.address}>Kerala, 676304</Text>
            </View>

            {/* right */}
            <View style={styles.section}>
              <Image src={base64Img || ''}
                style={{ 
                display: 'block', width: 80, height: 40
              }}/>
              <Text style={styles.address}>Site at Canara Bank Jn,</Text>
              <Text style={styles.address}>Vengara, Malappuram,</Text>
              <Text style={styles.address}>Kerala, 676304</Text>
              <Text style={styles.address}>hijazicosmetics@gmail.com</Text>
            </View>
          </View>

          {/* content */}
          <View style={[styles.content, {marginTop: 50}]}>
            
            {/* head */}
            <View style={{...styles.tableRow, backgroundColor: '#dfdfdf', textAlign: 'right', marginBottom: 10}}>
              <Text style={{ width: '5%', textAlign: 'left'}}>No</Text>
              <Text style={{ width: '50%', textAlign: 'left'}}>Description</Text>
              <Text style={{ width: '15%'}}>Quantity</Text>
              <Text style={{ width: '15%'}}>Unit Price</Text>
              <Text style={{ width: '15%'}}>Tax</Text>
              <Text style={{ width: '15%'}}>Total</Text>
            </View>

            {/* items */}
            {invoice?.items?.map((item, i) => 
              (
                <View key={item?.id} style={{...styles.tableRow, textAlign: 'right'}}>
                  <Text style={{ width: '5%', textAlign: 'left'}}>{++i}</Text>
                  <Text style={{ width: '50%', textAlign: 'left', textTransform: 'capitalize'}}>{item?.name}</Text>
                  <Text style={{ width: '15%'}}>{item?.quantity}</Text>
                  <Text style={{ width: '15%'}}>₹{Number(item?.price).toFixed(2)}</Text>
                  <Text style={{ width: '15%'}}>₹{Number(item?.tax).toFixed(2)}</Text>
                  <Text style={{ width: '15%'}}>₹{Number(item?.total).toFixed(2)}</Text>
                </View>
              )
            )}

            {/* totals */}
            <View style={styles.totals}>
              <View style={{ width: '40%' }}>
                {invoice && Object.keys(invoice?.totals)?.map(key => 

                  <View key={key} style={{
                    ...styles.tableRow, width: '100%', 
                    justifyContent: 'space-between',
                    marginTop: key === 'total' ? 5 : 0,
                    paddingTop: key === 'total' ? 5 : 0,
                    borderTop: key === 'total' && '1px solid #dfdfdf'
                  }}
                  
                  >
                    <Text style={{
                      textTransform: 'capitalize',
                      fontWeight: key === 'total' ? 'bold' : 'normal',
                      fontSize: key === 'total' && 15,
                    }}>{key}</Text>
                    <Text
                      style={{ 
                        fontWeight: key === 'total' ? 'bold' : 'normal', 
                        fontSize: key === 'total' && 15, 
                      }}
                    >
                      {`${key === 'discounts' || key === 'roundOff' ? '- ' : ''}₹${Number(invoice?.totals[key]).toFixed(2)}`}
                    </Text>

                  </View>

                )}
              </View>
            </View>
          </View>
        </View>

        <View style={styles.footer}>
          <Text style={{ textAlign: 'center' }}>Thank you for purachasing and support</Text>
        </View>
      </Page>
    </Document>
  )

  /* download setup */
  const handleDownload = async() => {
    
    if(invoice){
      try {

        setIsLoading(true)

        const blob = await pdf(Doc).toBlob();
        const url = URL.createObjectURL(blob);
        const link = document.createElement('a');
        link.href = url;
        link.download = 'invoice.pdf';
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);

        URL.revokeObjectURL(url);

      } catch (error) {
        console.error('Error generating PDF:', error);
      }finally{
        setIsLoading(false);
      }
    }


  };

  return (
    <AnimatePresence>
      {isOpen && <Modal isOpen={isOpen}>
        <div className='w-200 h-110 flex flex-col space-y-10 overflow-hidden'>

          {/* invoice */}
          <div className="flex h-full relative">
            {!invoice || isDocLoading &&
              (
                <div className='w-full h-full text-center absolute inset-0 flex items-center justify-center'>
                  <MyFadeLoader size={30} radius={5} width={15} height={15} color='var(--color-primary-300)' />
                </div>
              )
            }
            <PDFViewer width="100%" height="100%" showToolbar={false}>
              {Doc}
            </PDFViewer>
          </div>

          {/* action buttons */}
          <div className='flex w-full items-center justify-end gap-2 pb-2 pr-2'>
            
            <button
              onClick={handleclose}
              className={`px-4! rounded-3xl! inline-flex items-center
              transition-all duration-300 !text-gray-500 hover:!text-white !bg-gray-300 hover:!bg-gray-400`}>

              <span>Close</span>
            </button>

            <LoadingButton
              onClick={handleDownload}
              loading={isLoading}
              text='Download'
              loadingText='Downloading . . . . .'
              icon={<ClipLoader color="white" size={23} />}
              className={`px-4! rounded-3xl! inline-flex items-center smooth !bg-primary-400`}
            />
          </div>
        </div>
      </Modal>}
    </AnimatePresence>
  )
}

const SaleInvoice = React.memo(SaleInvoiceComponent);

export default SaleInvoice